TReX.class.druid = function(aff)
-- load new prio list if t.class.druid.enabled then for k, v in pairs(TReX.prios.druid) do TReX.prios.switchPrios(k,TReX.prios.druid[k], 2) end end
t.serverside.affs2tree4 = {"brokenleftleg","brokenrightleg","brokenleftarm","brokenrightarm","concussion" ,"anorexia", "slickness", "asthma", "impatience","scytherus","hypochondria","voyria","darkshade","clumsiness"}
if t.serverside["settings"].tree then if TReX.serverside.numberOfLockAffs() >= TReX.affs2tree["_by_class"]["druid"] then raiseEvent("trackable_event got")
end end
end

